import React, { useEffect, useState,useContext } from 'react'
import Axios from "axios"
import { UserContext } from '../Context/UserContext'
import axios from 'axios'

function CartItem(props) {
    const { shoppingList} = useContext(UserContext)
    const { setShoppingList} = useContext(UserContext)
    const [cartItem,setCartItem] = useState()
    const [price,setPrice] = useState()
    const[options,setOptions]= useState()
    function renderSelect (){
        let arr = []
        let i
        for(i=1;i<props.item.quantity+4;i++){
                arr.push(i)
        }
        setOptions(arr)
    }

    useEffect(()=>{
        console.log(props.item)
       axios.get(`http://localhost:5000/GetAllItems`).then((res)=>{
        res.data.map((item)=>{
            if(item._id === props.item.itemId){
           let price = item.price* parseInt(props.item.quantity)
                setCartItem(item)
                setPrice(price)
                        }
         
        
        })
       })

       renderSelect ()
    },[shoppingList])
  return (
    <div className='cartItem'>
    <button><i class="fa-sharp fa-solid fa-xmark"></i></button>
    <img src={require(`../../../images/${props.item.imageId}`) }></img>
    <h1>{cartItem&&cartItem.name}</h1>
    <div>
    <h2>Quantity</h2>
        <select onChange={(e)=>{

                let arr = []
let quantity = parseInt(e.target.value) 
            let newItem = {
                imageId: props.item.imageId,
                itemId: props.item.itemId,
                quantity: quantity
            }
            shoppingList.map((item)=>{
                if(item.itemId !== newItem.itemId){
                    arr.push(item)
                }
            })

                arr.push(newItem)
                setShoppingList(arr)
        }} 
        
        id="quantity" name="quantity">
            { options && options.map((option)=>{
                if(option === props.item.quantity){
                    return <option key={option} selected value={option}>{option}</option>
                }else{
                    return <option key={option} value={option}>{option}</option>
                }
               
            })}

 
        </select>
    </div>
    <div>
        <h2>Price</h2>
       <p>{price&&price}$</p> 
    </div>
</div>
  )
}

export default CartItem
