import React from 'react'
import { useNavigate } from 'react-router-dom'
import  { useContext, useEffect,useState } from 'react'
import { UserContext } from '../Context/UserContext'
import axios from 'axios'
function UpdateProfile(props) {
    
  const navigate = useNavigate()
  const [user,setUser]=useState()
    const {userId} = useContext(UserContext)
    const {setUserId} = useContext(UserContext)
    const [date,setDate] = useState()
    const [email,setEmail] = useState()
    const [password,setPassword]   = useState()
    const [checkCassword,setCheckPassword]   = useState()
    const [name,setName]   = useState()
    const [error,setError] = useState()
    const {setUserName} = useContext(UserContext)
    const {loggedIn} = useContext(UserContext)
    const {shoppingList} = useContext(UserContext)
    const {RendersetShoppingList} = useContext(UserContext)
    useEffect(()=>{
        RendersetShoppingList(shoppingList)
    if(loggedIn === false){
      navigate("/")
    }
      if(userId){
        axios.get( `http://localhost:5000/GetUser/${userId}`).then((res)=>{
      console.log(res.data)
          setUser(res.data)
          setDate(res.data.createdAt.slice(0,10))
    
        })
      }},[])
  return (
    <form className='profilePageProfileSettings'>
    <h1>{error}</h1>
      <div className='profilePageProfileSettingsDiv'>
            <div> 
               <label for="text"> NEW USERNAME:</label>
                <input onChange={(e)=>{
                 setName( e.target.value)
                }} type="text" name="text" id="text"></input>
             </div>
   
             <div> 
               <label for="email"> NEW EMAIL:</label>
                <input  onChange={(e)=>{
                 setEmail( e.target.value)
                }}type="email" name="email" id="email"></input>
             </div>
       </div>
  
  
  
       <div className='profilePageProfileSettingsDiv'>
            <div> 
               <label for="password"> NEW PASSWORD:</label>
                <input onChange={(e)=>{
                 setPassword( e.target.value)
                }} type="password" name="password" id="password"></input>
             </div>
   
             <div> 
               <label for="newPassword"> CONFIRM NEW PASSWORD:</label>
                <input  onChange={(e)=>{
                 setCheckPassword( e.target.value)
                }}type="password" name="newPassword" id="newPassword"></input>
             </div>
       </div>
  
       <button className="profilePageUpdateProfileButton"  onClick={(e)=>{
        e.preventDefault()
        let newUser={
          email,password,name
        };
        if(!password   && !checkCassword ) {
          if(email === undefined){
             
            newUser.email = user.email
          }else{
            newUser.email = email
          }
       
            newUser.password = password
          
          if(name === undefined){
            newUser.name = user.name
          }else{
            newUser.name = name
          }
          axios.post( `http://localhost:5000/UpdateUser/${userId}`,newUser).then(res=>{
    
            props.setUser(res.data) 
            props.setUserId(res.data._id)
            props.setUserName(res.data.name)
          })
  
        }
      else{
        if(checkCassword === undefined){
          props.setError("Please verify your password")
        }
         else{
            if(password === checkCassword){
              if(email === undefined){
               
                newUser.email = user.email
              }else{
                newUser.email = email
              }
              if(password === undefined){
                newUser.password = user.password
              }else{
                newUser.password = password
              }
              if(name === undefined){
                newUser.name = user.name
              }else{
                newUser.name = name
              }
  
              axios.post( `http://localhost:5000/UpdateUser/${userId}`,newUser).then(res=>{
                props.setUser(res.data) 
                props.setUserId(res.data._id)
                props.setUserName(res.data.name)
              })
  
  
            }else{
              props.setError("Passowrds do not match")
            }
          }
            
            
        }
  
  
       }}>Update Profile</button>
  </form>
  )
}

export default UpdateProfile
