import React, { useEffect, useState,useContext } from 'react'
import { UserContext } from '../Context/UserContext'
import axios from 'axios'   
import { useNavigate } from 'react-router-dom'

function OrderPageOrder(props) {
    const [total,setTotal] = useState()
    useEffect(()=>{
        let total =0
        let add = 0
        props.order.items.map((item)=>{
            axios.get(`http://localhost:5000/GetItem/${item.itemId}`).then((res)=>{
              add =parseInt(res.data.price) * parseInt(item.quantity)
             
               total=parseInt(total)+ parseInt(add)
            })
        })
        console.log(total)
    })
  return (
 
    <div className={props.order.payed ?'ordersListPageOrder orderPayed':  "ordersListPageOrder orderNotPayed"}>
    
    <div className='ordersListPageOrderBigDiv'>
    {props.order._id}
    
    </div>

    <div className='ordersListPageOrderSmallDiv'>
    {props.order.payed ?'Payed':  "Not Payed"}
   
    </div>

    <div className='ordersListPageOrderSmallDiv'>
    {props.order.createdAt}
   
    </div>

    <div className='ordersListPageOrderSmallDiv'>

    </div>

  </div>
  )
}

export default OrderPageOrder
