    <label for="email">Email</label>
    <input type="email" name="email" id="email"></input>
   </div>
   <div  className='formLabelInput'>
    <label for="cardNumber">Card Number</label>
    <input type="string" name="cardNumber" id="cardNumber"></input>
   </div>
   <div  className='formLabelInput'>
    <label for="expireDateOfCard">Card Expire Date</label>
    <input type="string" maxLength={5} name="expireDateOfCard" id="expireDateOfCard"></input>
   </div>
   <div  className='formLabelInput'>
    <label for="cv">Card CV</label>
    <input type="string" maxLength={3} name="cv" id="cv"></input>
   </div>
        <button 
        onClick={(e)=>{
            e.preventDefault()
        
        }}className='payFormBTN'>Pay For Order</button>
    </form>
    </div>
  )
}

export default PayForm
