import React, { useEffect, useState,useContext } from 'react'
import Axios from "axios"
import { UserContext } from '../Context/UserContext'
import axios from 'axios'

function CartItem(props) {
    const [cartItem,setCartItem] = useState()
    const [price,setPrice] = useState()
    useEffect(()=>{
        console.log(props.item)
       axios.get(`http://localhost:5000/GetAllItems`).then((res)=>{
        res.data.map((item)=>{
            if(item._id === props.item.itemId){
           let price = item.price* parseInt(props.item.quantity)
                setCartItem(item)
                setPrice(price)
                        }
         
        
        })
       })
    },[])
  return (
    <div className='cartItem'>
    <button><i class="fa-sharp fa-solid fa-xmark"></i></button>
    <img></img>
    <h1>{cartItem&&cartItem.name}</h1>
    <div>
    <h2>Quantity</h2>
        <select  id="quantity" name="quantity">
        <option value={props.item.quantity -3}>{props.item.quantity -3}</option>
 <option value={props.item.quantity -2}>{props.item.quantity -2}</option>
<option value={props.item.quantity -1}>{props.item.quantity -1}</option>
<option value={props.item.quantity}>{props.item.quantity}</option>
<option value={props.item.quantity +1}>{props.item.quantity +1}</option>
 <option value={props.item.quantity +2}>{props.item.quantity +2}</option>
<option value={props.item.quantity +3}>{props.item.quantity +3}</option>

 
        </select>
    </div>
    <div>
        <h2>Price</h2>
       <p>{price&&price}$</p> 
    </div>
</div>
  )
}

export default CartItem
